import {Container} from "react-bootstrap";
import React, {useRef, useState} from 'react';
import io from 'socket.io-client';

function MainPage(props) {

	const socket = io();
  const myFaceRef = useRef(null);
  const muteRef = useRef(null);
  const cameraRef = useRef(null);
  const camerasRef = useRef(null);
  const callRef = useRef(null);
  const welcomeRef = useRef(null);
  const welcomeFormRef = useRef(null);
  const peerFaceRef = useRef(null);
  const [hidden, setHidden] = useState(true);
  const [welHidden, setWelHidden] = useState(false);

  // 추가
  const streamBoxRef = useRef(null);
  const streamsRef = useRef(null);
  const myStreamRef = useRef(null);
  const userNickNameRef = useRef(null);
  const controllersRef = useRef(null);
  const buttonsRef = useRef(null);
  const chatRoomRef = useRef(null);
  const chatBoxRef = useRef(null);
  const chatFormRef = useRef(null);
  const roomNameRef = useRef(null);
  const nickNameRef = useRef(null);

  const HIDDEN = "hidden";




  let myStream;
  let muted = false;
  let cameraOff = false;
  let roomName = "";
  let nickName = "";
  let myPeerConnection;
  let myDataChannel;
  let peopleInRoom = 1;
  let pcObj = {};

async function getCameras() {
try {
  const devices = await navigator.mediaDevices.enumerateDevices();
  const cameras = devices.filter((device) => device.kind === "videoinput");
  const currentCamera = myStream.getVideoTracks()[0];
  cameras.forEach((camera) => {
    const option = document.createElement("option");
    option.value = camera.deviceId;
    option.innerText = camera.label;
    if (currentCamera.label === camera.label) {
      option.selected = true;
    }
    camerasRef.current.appendChild(option);
  });
} catch (e) {
  console.log(e);
}
}

async function getMedia(deviceId) {
const initialConstrains = {
  audio: true,
  video: { facingMode: "user" },
};
const cameraConstraints = {
  audio: true,
  video: { deviceId: { exact: deviceId } },
};
try {
  myStream = await navigator.mediaDevices.getUserMedia(
    // deviceId가 있다면 특정 camera로, 없다면 셀프 카메라로 설정
    deviceId ? cameraConstraints : initialConstrains
  );
  myFaceRef.current.srcObject = myStream;
  myFaceRef.current.muted = true;
  if (!deviceId) {
    myStream
      .getAudioTracks()
      .forEach((track) => (track.enabled = false));
    await getCameras();
  }
} catch (e) {
  console.log(e);
}
}

function handleMuteClick() {
myStream
  .getAudioTracks()
  .forEach((track) => (track.enabled = !track.enabled));
if (!muted) {
  muteRef.class.innerText = "Unmute";
  muted = true;
} else {
  muteRef.class.innerText = "Mute";
  muted = false;
}
}
function handleCameraClick() {
myStream
  .getVideoTracks()
  .forEach((track) => (track.enabled = !track.enabled));
if (cameraOff) {
  cameraRef.current.innerText = "Turn Camera Off";
  cameraOff = false;
} else {
  cameraRef.current.innerText = "Turn Camera On";
  cameraOff = true;
}
}

async function handleCameraChange() {
// select 한 camera id를 기준으로 다시 getMedia를 통해 미디어 스트림을 가져옴
await getMedia(camerasRef.current.value);
if (myPeerConnection) {
  const videoTrack = myStream.getVideoTracks()[0];
  const videoSender = myPeerConnection
    .getSenders()
    .find((sender) => sender.track.kind === "video");
  videoSender.replaceTrack(videoTrack);
}
}




async function initCall() {
setWelHidden(true);
setHidden(false);
await getMedia();
makeConnection();
}





// 추가


// let captureStream = null;
// async function startCapture() {
//   try {
//     captureStream = await navigator.mediaDevices.getDisplayMedia({
//       video: true,
//       audio: true,
//     });

//     const screen
//   }
// }








async function handleWelcomeSubmit(event) {
event.preventDefault();
const welcomeRoomName = roomNameRef.current;
const welcomeNickName = nickNameRef.current;
const nickNameContainer = userNickNameRef.current;
if(socket.disconnected) {
  socket.connect();
}
// await initCall();
roomName = welcomeRoomName.value;
welcomeRoomName.value = "";
nickName = welcomeNickName.value;
welcomeNickName.value = "";
nickNameContainer.innerText = nickName;
socket.emit("join_room", roomName, nickName);
}






// Socket code

socket.on("reject_join", () => {
  // paintModal

  const nickNameContainer = userNickNameRef.current;
  nickNameContainer.innerText = "";
  roomName = "";
  nickName = "";
});

socket.ion("accept_join", async(userObjArr) => {
  await initCall();
  const length = userObjArr.length;
  if(length === 1) {
    return;
  }

  // writeChat
  for(let i = 0; i < length - 1; i++) {
    try {
      const newPC = createConnection(
        userObjArr[i].socketId,
        userObjArr[i].nickName
      );
      const offer = await newPC.createOffer();
      await newPC.setLocalDescription(offer);
      socket.emit("offer", offer, userObjArr[i].socketId, nickName);
      // wirteChat
    } catch(err) {
      console.error(err);
    }
  }
  // writeChat
});


socket.on("offer", async (offer, remoteSocketId, remoteNickName) => {
  try {
    const newPC = createConnection(remoteSocketId, remoteNickName);
    await newPC.setRemoteDescription(offer);
    const answer = await newPC.createAnswer();
    await newPC.setLocalDescription(answer);
    socket.emit("answer", answer, remoteSocketId);
    // writeChat
  } catch(err) {
    console.error(err);
  }
});

socket.on("answer", async (answer, remoteSocketId) => {
  // console.log("received the answer");
  await pcObj[remoteSocketId].setRemoteDescription(answer);
});


socket.on("ice", async (ice, remoteSocketId) => {
  await pcObj[remoteSocketId].addIceCandidate(ice);
});

// socket.on(chat)

// socket.on("leave")





// RTC Code

function createConnection(remoteSocketId, remoteNickName) {
  myPeerConnection = new RTCPeerConnection({
    iceServers: [
      {
        urls: [
          "stun:stun.l.google.com:19302",
          "stun:stun1.l.google.com:19302",
          "stun:stun2.l.google.com:19302",
          "stun:stun3.l.google.com:19302",
          "stun:stun4.l.google.com:19302",
        ],
      },
    ],
  });
  myPeerConnection.addEventListener("icecandidate", (event) => {
    handleIce(event, remoteSocketId);
  });
  myPeerConnection.addEventListener("icecandidate", (event) => {
    handleTrack(event, remoteSocketId, remoteNickName);
  });
  // myPeerConnection.addEventListener("track", handleTrack); // 이 부분이 추가됩니다.
  myStream.getTracks().forEach((track) => myPeerConnection.addTrack(track, myStream));
  pcObj[remoteSocketId] = myPeerConnection;

  ++peopleInRoom;
  sortStreams();
  return myPeerConnection;
}

function handleIce(event, remoteSocketId) {
  if(event.candidate) {
    socket.emit("ice", event.candidate, remoteSocketId);
  }
}
// function handleAddStream(data) {
//   if (peerFaceRef.current) {
//     peerFaceRef.current.srcObject = data.stream;
//   } else {
//     console.log('peerFaceRef.current is not defined');
//   }
// }
function handleTrack(event, remoteSocketId, remoteNickName) {
  const peerStream = event.stream;
  paintPeerFace(peerStream, remoteSocketId, remoteNickName);
}

function paintPeerFace(peerStream, id, remoteNickName) {
  const div = document.createElement("div");
  div.id = id;
  const video = document.createElement("video");
  video.autoplay = true;
  video.playsInline = true;
  video.width = "400";
  video.height = "400";
  video.srcObject = peerStream;
  const nickNameContainer = document.createElement("h3");
  nickNameContainer.id = "userNickName";
  nickNameContainer.innerText = remoteNickName;

  div.appendChild(video);
  div.appendChild(nickNameContainer);
  streamsRef.current.appendChild(div);
  sortStreams();
}

function sortStreams() {
  const streamArr = streamsRef.current.querySelectorAll("div");
  streamArr.forEach((stream) => (stream.className = `people${peopleInRoom}`));
}

	return (
		<Container>
			<h1>영상통화</h1>
			<div className="video">
        <div>
            <div id="welcome" ref={welcomeRef} 
                style={{display: welHidden ? "none" : "block"}}
            >
              <form ref={welcomeFormRef}>
                <input ref={roomNameRef} placeholder="room name" required type="text"/>
                <input ref={nickNameRef} placeholder="nick name" type="text"/>
                <button onClick={handleWelcomeSubmit}>Enter room</button>
              </form>
            </div>
            <div id="call" ref={callRef}
                style={{ display: hidden ? "none" : "block" }}
            >

              <div ref={streamBoxRef}>
                <div ref={streamsRef}>
                  <div ref={myStream} className="people1">
                    <video id="myFace"ref={myFaceRef} autoPlay playsInline width="400" height="400"></video>
                    <h3 ref={userNickNameRef}></h3>
                  </div>
                </div>
                <div ref={controllersRef}>
                  <div>
                    <select id="cameras" ref={camerasRef} onChange={handleCameraChange}></select>
                  </div>
                  <div>
                    <div ref={buttonsRef}>
                      <button id="mute" ref={muteRef} onClick={handleMuteClick}>Mute</button>
                      <button id="camera" ref={cameraRef} onClick={handleCameraClick}>Turn Camera Off</button>
                    </div>
                  </div>
                  <div>
                    <button>Leave</button>
                  </div>
                </div>
              </div>

              <div ref={chatRoomRef}>
                <ul ref={chatBoxRef}>
                  <form ref={chatFormRef}>
                    <input type="text" placeholder="Write your chat"/>
                    <button>Send</button>
                  </form>
                </ul>
              </div>
            </div>
        </div>
			</div>
		</Container>
	);
}

export default MainPage;
