import {Container} from "react-bootstrap";
import React, {useRef, useState} from 'react';
import io from 'socket.io-client';


function MainPage(props) {

	const socket = io();
  const myFaceRef = useRef(null);
  const muteRef = useRef(null);
  const cameraRef = useRef(null);
  const camerasRef = useRef(null);
  const callRef = useRef(null);
  const welcomeRef = useRef(null);
  const welcomeFormRef = useRef(null);
  // const peerFaceRef = useRef(null);
  const [hidden, setHidden] = useState(true);
  const [welHidden, setWelHidden] = useState(false);

  // 추가
  const streamBoxRef = useRef(null);
  const streamsRef = useRef(null);
  const myStreamRef = useRef(null);
  const userNickNameRef = useRef(null);
  const controllersRef = useRef(null);
  const buttonsRef = useRef(null);
  const chatRoomRef = useRef(null);
  const chatBoxRef = useRef(null);
  const chatFormRef = useRef(null);
  const roomNameRef = useRef(null);
  const nickNameRef = useRef(null);

  // const HIDDEN = "hidden";



  let myPeerConnection = null;
  let myStream;
  let muted = false;
  let cameraOff = false;
  let roomName = "";
  let nickname = "";

  let peopleInRoom = 1;
  let pcObj = {};
  let peerConnectionObjArr = [];



async function getCameras() {
  try {
    const devices = await navigator.mediaDevices.enumerateDevices();
    const cameras = devices.filter((device) => device.kind === "videoinput");
    const currentCamera = myStream.getVideoTracks();
    cameras.forEach((camera) => {
      const option = document.createElement("option");
      option.value = camera.deviceId;
      option.innerText = camera.label;

      if (currentCamera.label == camera.label) {
        option.selected = true;
      }

      camerasRef.current.appendChild(option);
    });
  } catch (error) {
    console.log(error);
  }
}

async function getMedia(deviceId) {
  const initialConstraints = {
    audio: true,
    video: { facingMode: "user" },
  };
  const cameraConstraints = {
    audio: true,
    video: { deviceId: { exact: deviceId } },
  };

  try {
    myStream = await navigator.mediaDevices.getUserMedia(
      deviceId ? cameraConstraints : initialConstraints
    );

    // stream을 mute하는 것이 아니라 HTML video element를 mute한다.
    myFaceRef.current.srcObject = myStream;
    myFaceRef.current.muted = true;

    if (!deviceId) {
      // mute default
      myStream //
        .getAudioTracks()
        .forEach((track) => (track.enabled = false));

      await getCameras();
    }
  } catch (error) {
    console.log(error);
  }
}

function handleMuteClick() {
  myStream //
    .getAudioTracks()
    .forEach((track) => (track.enabled = !track.enabled));
  if (muted) {
    // unMuteIcon.classList.remove(HIDDEN_CN);
    // muteIcon.classList.add(HIDDEN_CN);
    muted = false;
  } else {
    // muteIcon.classList.remove(HIDDEN_CN);
    // unMuteIcon.classList.add(HIDDEN_CN);
    muted = true;
  }
}

function handleCameraClick() {
  myStream //
    .getVideoTracks()
    .forEach((track) => (track.enabled = !track.enabled));
  if (cameraOff) {
    // cameraIcon.classList.remove(HIDDEN_CN);
    // unCameraIcon.classList.add(HIDDEN_CN);
    cameraOff = false;
  } else {
    // unCameraIcon.classList.remove(HIDDEN_CN);
    // cameraIcon.classList.add(HIDDEN_CN);
    cameraOff = true;
  }
}

async function handleCameraChange() {
  try {
    await getMedia(camerasRef.current.value);
    if (peerConnectionObjArr.length > 0) {
      const newVideoTrack = myStream.getVideoTracks()[0];
      peerConnectionObjArr.forEach((peerConnectionObj) => {
        const peerConnection = peerConnectionObj.connection;
        const peerVideoSender = peerConnection
          .getSenders()
          .find((sender) => sender.track.kind == "video");
        peerVideoSender.replaceTrack(newVideoTrack);
      });
    }
  } catch (error) {
    console.log(error);
  }
}


/////////////////////////////////// prototype
// Screen Sharing

// let captureStream = null;

// async function startCapture() {
//   try {
//     captureStream = await navigator.mediaDevices.getDisplayMedia({
//       video: true,
//       audio: true,
//     });

//     const screenVideo = document.querySelector("#screen");
//     screenVideo.srcObject = captureStream;
//   } catch (error) {
//     console.error(error);
//   }
// }

// Welcome Form (choose room)

// call.classList.add(HIDDEN_CN);
// welcome.hidden = true;



async function initCall() {
  setWelHidden(true);
  setHidden(false);
  // welcome.hidden = true;
  // call.classList.remove(HIDDEN_CN);
  await getMedia();
}

async function handleWelcomeSubmit(event) {
  event.preventDefault();

  if (socket.disconnected) {
    socket.connect();
  }

  const welcomeRoomName = roomNameRef.current;
  const welcomeNickname = nickNameRef.current
  const nicknameContainer = userNickNameRef.current;
  roomName = welcomeRoomName.value;
  welcomeRoomName.value = "";
  nickname = welcomeNickname.value;
  welcomeNickname.value = "";
  nicknameContainer.innerText = nickname;
  socket.emit("join_room", roomName, nickname);
}



// Chat Form

const chatForm = chatFormRef.current;
const chatBox = chatBoxRef.current;

const MYCHAT_CN = "myChat";
const NOTICE_CN = "noticeChat";



function handleChatSubmit(event) {
  event.preventDefault();
  const chatInput = chatForm.querySelector("input");
  const message = chatInput.value;
  chatInput.value = "";
  socket.emit("chat", `${nickname}: ${message}`, roomName);
  writeChat(`You: ${message}`, MYCHAT_CN);
}

function writeChat(message, className = null) {
  const li = document.createElement("li");
  const span = document.createElement("span");
  span.innerText = message;
  li.appendChild(span);
  // li.classList.add(className);
  chatBox.prepend(li);
}

// Leave Room

const leaveBtn = document.querySelector("#leave");

function leaveRoom() {
  socket.disconnect();

  // call.classList.add(HIDDEN_CN);
  setWelHidden(false);

  peerConnectionObjArr = [];
  peopleInRoom = 1;
  nickname = "";

  myStream.getTracks().forEach((track) => track.stop());
  const nicknameContainer = document.querySelector("#userNickname");
  nicknameContainer.innerText = "";

  myFaceRef.current.srcObject = null;
  clearAllVideos();
  clearAllChat();
}

function removeVideo(leavedSocketId) {
  const streams = streamsRef.current;
  const streamArr = streams.querySelectorAll("div");
  streamArr.forEach((streamElement) => {
    if (streamElement.id === leavedSocketId) {
      streams.removeChild(streamElement);
    }
  });
}

function clearAllVideos() {
  const streams = streamsRef.current;
  const streamArr = streams.querySelectorAll("div");
  streamArr.forEach((streamElement) => {
    if (streamElement.id != "myStream") {
      streams.removeChild(streamElement);
    }
  });
}

function clearAllChat() {
  const chatArr = chatBox.querySelectorAll("li");
  chatArr.forEach((chat) => chatBox.removeChild(chat));
}

// leaveBtn.addEventListener("click", leaveRoom);

// Modal code

// const modal = document.querySelector(".modal");
// const modalText = modal.querySelector(".modal__text");
// const modalBtn = modal.querySelector(".modal__btn");

function paintModal(text) {
  // modalText.innerText = text;
  // modal.classList.remove(HIDDEN_CN);

