import {Container} from "react-bootstrap";
import React, {useRef, useState} from 'react';
import io from 'socket.io-client';

function MainPage(props) {

	const socket = io();
    const myFaceRef = useRef(null);
    const muteRef = useRef(null);
    const cameraRef = useRef(null);
    const camerasRef = useRef(null);
    const callRef = useRef(null);
    const welcomeRef = useRef(null);
    const welcomeFormRef = useRef(null);
    const peerFaceRef = useRef(null);
    const myStreamRef = useRef(null);
    const [hidden, setHidden] = useState(true);
    const [welHidden, setWelHidden] = useState(false);

    let myStream;
    let muted = false;
    let cameraOff = false;
    let roomName;
    let myPeerConnection;
    let myDataChannel;

    let pcObj = {};
async function getCameras() {
  try {
    const devices = await navigator.mediaDevices.enumerateDevices();
    const cameras = devices.filter((device) => device.kind === "videoinput");
    const currentCamera = myStream.getVideoTracks()[0];
    cameras.forEach((camera) => {
      const option = document.createElement("option");
      option.value = camera.deviceId;
      option.innerText = camera.label;
      if (currentCamera.label === camera.label) {
        option.selected = true;
      }
      camerasRef.current.appendChild(option);
    });
  } catch (e) {
    console.log(e);
  }
}

async function getMedia(deviceId) {
  const initialConstrains = {
    audio: true,
    video: { facingMode: "user" },
  };
  const cameraConstraints = {
    audio: true,
    video: { deviceId: { exact: deviceId } },
  };
  try {
    myStream = await navigator.mediaDevices.getUserMedia(
      // deviceId가 있다면 특정 camera로, 없다면 셀프 카메라로 설정
      deviceId ? cameraConstraints : initialConstrains
    );
    myFaceRef.current.srcObject = myStream;
    if (!deviceId) {
      await getCameras();
    }
  } catch (e) {
    console.log(e);
  }
}

function handleMuteClick() {
  myStream
    .getAudioTracks()
    .forEach((track) => (track.enabled = !track.enabled));
  if (!muted) {
    muteRef.class.innerText = "Unmute";
    muted = true;
  } else {
    muteRef.class.innerText = "Mute";
    muted = false;
  }
}
function handleCameraClick() {
  myStream
    .getVideoTracks()
    .forEach((track) => (track.enabled = !track.enabled));
  if (cameraOff) {
    cameraRef.current.innerText = "Turn Camera Off";
    cameraOff = false;
  } else {
    cameraRef.current.innerText = "Turn Camera On";
    cameraOff = true;
  }
}

async function handleCameraChange() {
  // select 한 camera id를 기준으로 다시 getMedia를 통해 미디어 스트림을 가져옴
  await getMedia(camerasRef.current.value);
  if (myPeerConnection) {
    const videoTrack = myStream.getVideoTracks()[0];
    const videoSender = myPeerConnection
      .getSenders()
      .find((sender) => sender.track.kind === "video");
    videoSender.replaceTrack(videoTrack);
  }
}




async function initCall() {
  setWelHidden(true);
  setHidden(false);
  await getMedia();
  myPeerConnection = makeConnection(); // 수정된 부분
}

async function handleWelcomeSubmit(event) {
  event.preventDefault();
  const input = welcomeFormRef.current.querySelector("input");
  await initCall();
  await getMedia();
  // 추후에 닉네임 추가
  socket.emit("join_room", input.value);
  roomName = input.value;
  input.value = "";
}



socket.on("welcome", async (userList) => {
  const length = userList.length;   //추가
  console.log("유저수: " + length); //추가
  for(let i = 0; i < length; i++) {
    try {
      myDataChannel = myPeerConnection.createDataChannel("chat");
      myDataChannel.addEventListener("message", (event) => console.log(event.data));
      const newpc = makeConnection(
        userList[i].sid,
        userList[i].nickname
      );
      console.log(`유저를 클라에서 출력: ${userList[i].sid}`);
      const offer = await newpc.createOffer();
      await newpc.setLocalDescription(offer);
      console.log("현재 연결된 유저들에게 offer 요청");
      socket.emit("offer", offer, userList[i].sid, userList[i].nickname);

    } catch(err) {
      console.error(err);
    } 
  }

});




socket.on("offer", async (offer, roomName) => {
  myPeerConnection.addEventListener("datachannel", (event) => {
    myDataChannel = event.channel;
    myDataChannel.addEventListener("message", (event) =>
      console.log(event.data)
    );
  });
  console.log("received the offer");
  myPeerConnection.setRemoteDescription(offer);
  const answer = await myPeerConnection.createAnswer();
  myPeerConnection.setLocalDescription(answer);
  socket.emit("answer", answer, roomName);
  console.log("sent the answer");
});

socket.on("answer", (answer, roomName) => {
  console.log("received the answer");
  myPeerConnection.setRemoteDescription(answer);
});

socket.on("ice", (ice, roomName) => {
  console.log("received candidate");
  myPeerConnection.addIceCandidate(ice);
});

// RTC Code

function makeConnection(sid, nickname) {
  const newPeerConnection = new RTCPeerConnection({
    iceServers: [
      {
        urls: [
          "stun:stun.l.google.com:19302",
          "stun:stun1.l.google.com:19302",
          "stun:stun2.l.google.com:19302",
          "stun:stun3.l.google.com:19302",
          "stun:stun4.l.google.com:19302",
        ],
      },
    ],
  });
  newPeerConnection.addEventListener("icecandidate", (event) => {
    handleIce(event, sid);
  });

newPeerConnection.addEventListener("track", (event) => {
  handleTrack(event, sid, nickname);
}); // 이 부분이 추가됩니다.
  myStream.getTracks().forEach((track) => newPeerConnection.addTrack(track, myStream));

  return newPeerConnection;
}

function handleIce(data, sid) {
  console.log("sent candidate");
  socket.emit("ice", data.candidate, sid);
}
// function handleAddStream(data) {
//   if (peerFaceRef.current) {
//     peerFaceRef.current.srcObject = data.stream;
//   } else {
//     console.log('peerFaceRef.current is not defined');
//   }
// }

function paintPeerFace(peerStream, sid, nickname) {
  const streams = myStreamRef.current;
  const divElement = document.createElement("div");
  divElement.id = sid;
  const videoElement = document.createElement("video");
  videoElement.autoplay = true;
  videoElement.playsInline = true;
  videoElement.width = "400";
  videoElement.height = "400";
  videoElement.srcObject = peerStream;
  divElement.appendChild(videoElement);
  streams.appendChild(divElement);

}

function handleTrack(event, sid, nickname) {
  const peerStream = event.streams[0];
  console.log(peerStream);
  paintPeerFace(peerStream, sid, nickname);


  // if (peerFaceRef.current) {
  //   peerFaceRef.current.srcObject = event.streams[0];
  // } else {
  //   console.log('peerFaceRef.current is not defined');
  // }
}

	return (
		<Container>
			<h1>영상통화</h1>
			<div className="video">
        <div>
            <div id="welcome" ref={welcomeRef} 
                style={{display: welHidden ? "none" : "block"}}
            >
                <form ref={welcomeFormRef}>
                    <input placeholder="room name" required type="text"/>
                    <button onClick={handleWelcomeSubmit}>Enter room</button>
                </form>
            </div>
            <div id="call" ref={callRef}
                style={{ display: hidden ? "none" : "block" }}
            >
                <div id="myStream" ref={myStreamRef}>
                    <video id="myFace"ref={myFaceRef} autoPlay playsInline width="400" height="400"></video>
                    <button id="mute" ref={muteRef} onClick={handleMuteClick}>Mute</button>
                    <button id="camera" ref={cameraRef} onClick={handleCameraClick}>Turn Camera Off</button>
                    <select id="cameras" ref={camerasRef} onChange={handleCameraChange}></select>
                    <video id="peerFace" ref={peerFaceRef} autoPlay playsInline width="400" height="400"></video>
                </div>
            </div>
            <button onClick={handleWelcomeSubmit}>teset</button>
        </div>
			</div>
		</Container>
	);
}

export default MainPage;
